export const supportedRegions = Array.from(
  new Set(universities.map((u) => u.region)),
);
export const supportedCountries = Array.from(
  new Set(universities.map((u) => u.countryCode)),
);

export function filterUniversities({
  region,
  countryCode,
}: {
  region?: UniversityRegion;
  countryCode?: string;
}) {
  return universities.filter((u) => {
    const regionOk = region ? u.region === region : true;
    const countryOk = countryCode ? u.countryCode === countryCode : true;
    return regionOk && countryOk;
  });
}
